function boardReseter(){
    for(let x=0;x<64;x++){
        if(redSquares.current[x]==='red'){redSquares.current[x]='default';}
    }
}
function realBoardReseter(){
    let chesRow=0;
    for(let x=0;x<64;x++){
        if(redSquares.current[x]==='red'){redSquares.current[x]='default';}
        if(x%2===0){
            if(x%8===0){chesRow++;}
            if(chesRow%2===1){document.getElementById(x.toString()).style.backgroundColor='white';}
            else{
                document.getElementById(x.toString()).style.backgroundColor='black';
            }
        }
        else{
            if(x%8===0){chesRow++;}
            if(chesRow%2===1){document.getElementById(x.toString()).style.backgroundColor='black';}
            else{
                document.getElementById(x.toString()).style.backgroundColor='white';
            }
        }
    }
}
function handleClick(e){
let placeholder=document.getElementById(e.currentTarget.id);
if(chessMover.length!==0 && placeholder.style.backgroundColor==='red'){
    if(chessSquares.current[Number(chessMover[0])]==='p'&&upside===false&&[0,1,2,3,4,5,6,7].includes(Number(e.currentTarget.id))){
        chessSquares.current[Number(e.currentTarget.id)]='q';chessSquares.current[Number(chessMover.pop())]='clear';
        realBoardReseter();
        turn.current=!turn.current;
    }
    else if(chessSquares.current[Number(chessMover[0])]==='P'&&upside===false&&[63,62,61,60,59,58,57,56].includes(Number(e.currentTarget.id))){
        chessSquares.current[Number(e.currentTarget.id)]='Q';chessSquares.current[Number(chessMover.pop())]='clear';
        realBoardReseter()();
        turn.current=!turn.current;
    }
    else{
        let temp=chessSquares.current[Number(e.currentTarget.id)];
        chessSquares.current[Number(e.currentTarget.id)]=document.getElementById(chessMover[0]).children[0].id;
        if(temp.toLowerCase()==='k'){
        if(temp===temp.toLowerCase()){verdict='black wins';}
        else{verdict='white wins';}
        }
        chessSquares.current[Number(chessMover.pop())]='clear';
        realBoardReseter(); 
        turn.current=!turn.current;
    }
    setUp(a=>a+1);
    return;
}
else if(chessMover.length!==0){
    realBoardReseter();
    chessMover.pop();
    return;
}
if(placeholder.children.length===0){return;}
if((placeholder.children[0].id===placeholder.children[0].id.toLowerCase())!=turn.current){return;}
let placeholderId=placeholder.children[0].id;
chessMover.push(e.currentTarget.id);
if(placeholderId.toLowerCase()==='q'){
       createQueenMoves(e.currentTarget.id,placeholderId,false);
}
else if(placeholderId.toLowerCase()==='k'){
    createKingMoves(e.currentTarget.id,placeholderId,false);
}
else if(placeholderId.toLowerCase()==='b'){createBishopMoves(e.currentTarget.id,placeholderId,false);}

else if(placeholderId.toLowerCase()==='r'){createRookMoves(e.currentTarget.id,placeholderId,false);}
else if(placeholderId.toLowerCase()==='n'){createHorseMoves(e.currentTarget.id,placeholderId,false);}
else if(placeholderId.toLowerCase()==='p'){createPawnMoves(e.currentTarget.id,placeholderId,false);}
}
function handleAIMove(parent){

    if(chessMover.length!==0 && redSquares.current[parent]==='red'){
        if(chessSquares.current[Number(chessMover[0])]==='p'&&upside===false&&[0,1,2,3,4,5,6,7].includes(parent)){
            chessSquares.current[parent]='q';chessSquares.current[Number(chessMover.pop())]='clear';
            boardReseter();
            turn.current=!turn.current;
        }
        else if(chessSquares.current[Number(chessMover[0])]==='P'&&upside===false&&[63,62,61,60,59,58,57,56].includes(parent)){
            chessSquares.current[parent]='Q';chessSquares.current[Number(chessMover.pop())]='clear';
            boardReseter();
            turn.current=!turn.current;
        }
        else{
            let temp=chessSquares.current[parent];
            chessSquares.current[parent]=chessSquares.current[Number(chessMover[0])];
            if(temp.toLowerCase()==='k'){
            if(temp===temp.toLowerCase()){verdict='black wins';}
            else{verdict='white wins';}
            }
            chessSquares.current[Number(chessMover.pop())]='clear';
            boardReseter(); 
            turn.current=!turn.current;
        }
        setUp(a=>a+1);
       return;
    }

    else if(chessMover.length!==0){
        boardReseter();
        chessMover.pop();
        return;
    }
    if(chessSquares.current[parent]==='clear'){return;}
    if((chessSquares.current[parent]===chessSquares.current[parent].toLowerCase())!=turn.current){return;}
    let placeholderId=chessSquares.current[parent]
    chessMover.push(parent.toString());
    if(placeholderId.toLowerCase()==='q'){
           createQueenMoves(parent.toString(),placeholderId,true);
    }
    else if(placeholderId.toLowerCase()==='k'){
        createKingMoves(parent.toString(),placeholderId,true);
    }
    else if(placeholderId.toLowerCase()==='b'){createBishopMoves(parent.toString(),placeholderId,true);}
    
    else if(placeholderId.toLowerCase()==='r'){createRookMoves(parent.toString(),placeholderId,true);}
    else if(placeholderId.toLowerCase()==='n'){createHorseMoves(parent.toString(),placeholderId,true);}
    else if(placeholderId.toLowerCase()==='p'){createPawnMoves(parent.toString(),placeholderId,true);}
}
function handleTestMove(parent){
    if(chessSquares.current[parent]==='clear'){return;}
    //if((chessSquares.current[parent]===chessSquares.current[parent].toLowerCase())!=turn.current){return;}
    let placeholderId=chessSquares.current[parent]
    if(placeholderId.toLowerCase()==='q'){
           createQueenMoves(parent.toString(),placeholderId,true);
    }
    else if(placeholderId.toLowerCase()==='k'){
        createKingMoves(parent.toString(),placeholderId,true);
    }
    else if(placeholderId.toLowerCase()==='b'){createBishopMoves(parent.toString(),placeholderId,true);}
    
    else if(placeholderId.toLowerCase()==='r'){createRookMoves(parent.toString(),placeholderId,true);}
    else if(placeholderId.toLowerCase()==='n'){createHorseMoves(parent.toString(),placeholderId,true);}
    else if(placeholderId.toLowerCase()==='p'){createPawnMoves(parent.toString(),placeholderId,true);}
}
function makeTestMove(array){
chessSquares.current[array[0][0]]='clear';
chessSquares.current[array[1][0]]=array[0][1];
}
function unMakeTestMove(array){
    chessSquares.current[array[0][0]]=array[0][1];
    chessSquares.current[array[1][0]]=array[1][1];
}
function evaluate(color){
    let bro =countMaterial(color);
    let step=countMaterial(!color);
    let result=bro-step;
    return result;
}
function countMaterial(color){
    let material=0;
    for(let x=0;x<chessSquares.current.length;x++){
        if(chessSquares.current[x]==='clear'){continue;}
        if((chessSquares.current[x]===chessSquares.current[x].toLowerCase())===color){
            switch(upside===color){
            case true:    
            switch(chessSquares.current[x].toLowerCase()){
            case'p':
                material+=100;
                break;
            case 'b':material+=300;
            break;
            case'n':material+=300; break;
            case'r':material+=600;
            break;
            case'q':material+=900;
            break;
            case 'k':material+=10000; break;
            default:
            }
            break;
            default:
                switch(chessSquares.current[x].toLowerCase()){
                    case'p':
                        material+=100;
                        break;
                    case 'b':material+=300;
                    break;
                    case'n':material+=300; break;
                    case'r':material+=600;
                    break;
                    case'q':material+=900;
                    break;
                    case 'k':material+=10000; break;
                    default:
                    }  
        }
        }
    }
    return material;
}
function makeMove(array){
    chessSquares.current[array[0][0]]='clear';
chessSquares.current[array[1][0]]=array[0][1];
turn.current=!turn.current;
setUp(a=>a+1);
}
function moveTester(color,depth,checkColor,theMove,initialDepth,alpha,beta){
if(depth===0){
    return [theMove,evaluate(checkColor)];
}
let list=[];
list=generateMove(color);
//let numbOfMoves=0;
if(color===checkColor){
let bestMove=[theMove,Number.NEGATIVE_INFINITY];
for(let x=0;x<list.length;x++){
//numbOfMoves+=moveTester(!color,depth-1,checkColor)
makeTestMove(list[x]);
let array=moveTester(!color,depth-1,checkColor,list[x],initialDepth,alpha,beta);
if(depth===initialDepth){
    array[0]=list[x]
    bestMove=(bestMove[1]>=array[1])===true?bestMove:array;
}
else{
    bestMove=(bestMove[1]>=array[1])===true?bestMove:array;
}
alpha=alpha>bestMove[1]?alpha:bestMove[1];
if(beta<=alpha){unMakeTestMove(list[x]);break;}
unMakeTestMove(list[x]);
}
return bestMove;
}
else{
let worstMove=[theMove,Number.POSITIVE_INFINITY];
    for(let x=0;x<list.length;x++){
        makeTestMove(list[x]);
        let array=moveTester(!color,depth-1,checkColor,list[x],initialDepth,alpha,beta);
            worstMove=(worstMove[1]<=array[1])?worstMove:array;
        beta=(beta<worstMove[1])?beta:worstMove[1];
        if(beta<=alpha){
            unMakeTestMove(list[x]);
            break;
        }
        unMakeTestMove(list[x]);
        }
        return worstMove;
}
}
